import React, { useState } from 'react';
import campData from './data.json';
import Campaign from './components/CampaignTable'; // Import Campaign component
import PricingModal from './components/PricingModal'; // Import PricingModal component
import ScheduleModal from './components/ScheduleModal'; // Import ScheduleModal component
import './App.css';

const App = () => {
  const [language, setLanguage] = useState('en'); // Track current language
  const [activeTab, setActiveTab] = useState('upcoming'); // Track active tab
  const [showPricingModal, setShowPricingModal] = useState(false); // Modal state for Pricing
  const [showScheduleModal, setShowScheduleModal] = useState(false); // Modal state for Scheduling
  const [selectedCampaign, setSelectedCampaign] = useState(null); // Track selected campaign

  const handleLanguageSwitch = () => {
    setLanguage(language === 'en' ? 'fr' : 'en');
  };

  // Handle opening modals
  const handleOpenPricingModal = (campaign) => {
    setSelectedCampaign(campaign);
    setShowPricingModal(true);
  };

  const handleOpenScheduleModal = (campaign) => {
    setSelectedCampaign(campaign);
    setShowScheduleModal(true);
  };

  // Handle closing modals
  const handleCloseModal = () => {
    setShowPricingModal(false);
    setShowScheduleModal(false);
    setSelectedCampaign(null);
  };

  const getCampaigns = (tab) => {
    return campData[tab].map((campaign) => ({
      ...campaign,
      name: campaign.name[language],
      region: campaign.region[language],
      price: campaign.price[language],
    }));
  };

  const campaigns = getCampaigns(activeTab);

  return (
    <div className="App">
      <header className="header">
        <h1>Manage Campaign</h1>
        <button className="language-switch" onClick={handleLanguageSwitch}>
          {language === 'en' ? 'Switch to French' : 'Passer au français'}
        </button>
      </header>

      <div className="navbar">
        <button
          onClick={() => setActiveTab('upcoming')}
          className={activeTab === 'upcoming' ? 'active' : ''}
        >
          Upcoming Campaigns
        </button>
        <button
          onClick={() => setActiveTab('live')}
          className={activeTab === 'live' ? 'active' : ''}
        >
          Live Campaigns
        </button>
        <button
          onClick={() => setActiveTab('past')}
          className={activeTab === 'past' ? 'active' : ''}
        >
          Past Campaigns
        </button>
      </div>

      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Campaign</th>
            <th>View</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {campaigns.map((campaign) => (
            <Campaign
              key={campaign.id}
              campaign={campaign}
              onViewPricing={handleOpenPricingModal}
              onSchedule={handleOpenScheduleModal}
            />
          ))}
        </tbody>
      </table>

      {/* Pricing Modal */}
      {showPricingModal && (
        <PricingModal
          campaign={selectedCampaign}
          onClose={handleCloseModal}
        />
      )}

      {/* Schedule Modal */}
      {showScheduleModal && (
        <ScheduleModal
          campaign={selectedCampaign}
          onClose={handleCloseModal}
        />
      )}
    </div>
  );
};

export default App;
