import React, { useState } from "react";
import { useTranslation } from "react-i18next";
import CampaignTable from "./components/CampaignTable";
import campaignsData from "./data/camp";

const App = () => {
  const { t, i18n } = useTranslation();
  const [campaigns, setCampaigns] = useState(campaignsData);
  const [language, setLanguage] = useState("en");

  const changeLanguage = (lng) => {
    setLanguage(lng);
    i18n.changeLanguage(lng);
  };

  const updateCampaign = (id, newDate) => {
    setCampaigns((prev) =>
      prev.map((campaign) =>
        campaign.id === id ? { ...campaign, createdOn: newDate.getTime() } : campaign
      )
    );
  };

  const filterCampaigns = (status) => {
    const now = Date.now();
    return campaigns.filter((campaign) =>
      status === "upcoming"
        ? campaign.createdOn > now
        : status === "live"
        ? campaign.createdOn <= now && campaign.createdOn > now - 24 * 60 * 60 * 1000
        : campaign.createdOn <= now - 24 * 60 * 60 * 1000
    );
  };

  return (
    <div className="App">
      <h1>{t("title")}</h1>
      <div>
        <button onClick={() => changeLanguage("en")}>English</button>
        <button onClick={() => changeLanguage("fr")}>Fran√ßais</button>
      </div>
      <div>
        <button>{t("upcoming")}</button>
        <button>{t("live")}</button>
        <button>{t("past")}</button>
      </div>
      <CampaignTable
        campaigns={filterCampaigns("upcoming")}
        updateCampaign={updateCampaign}
      />
    </div>
  );
};

export default App;
