import React, { useState, useEffect } from 'react';
import campData from './data.json'; // Import the data
import './App.css';

const App = () => {
  const [language, setLanguage] = useState('en'); // Track current language
  const [activeTab, setActiveTab] = useState('upcoming'); // Track active tab

  const handleLanguageSwitch = () => {
    setLanguage(language === 'en' ? 'fr' : 'en'); // Toggle between English and French
  };

  // Get campaigns based on active tab and language
  const getCampaigns = (tab) => {
    return campData[tab].map(campaign => ({
      ...campaign,
      name: campaign.name[language], // Show name in current language
      region: campaign.region[language], // Show region in current language
      price: campaign.price[language], // Show price in current language
    }));
  };

  const renderTable = () => {
    const campaigns = getCampaigns(activeTab);

    return (
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Campaign</th>
            <th>View</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {campaigns.map(campaign => (
            <tr key={campaign.id}>
              <td>{new Date(campaign.createdOn).toLocaleDateString()}</td>
              <td>{campaign.name}</td>
              <td>{campaign.price}</td>
              <td>
                <div className="actions">
                  <a href={campaign.csv}>CSV</a>
                  <a href={campaign.report}>Report</a>
                  <button>Schedule Again</button>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  };

  return (
    <div className="App">
      <div className="header">
        <h1>Manage Campaign</h1>
      </div>

      {/* Language Switcher */}
      <div className="language-switcher">
        <button onClick={handleLanguageSwitch}>
          {language === 'en' ? 'Switch to French' : 'Passer au français'}
        </button>
      </div>

      {/* Event Type Buttons in Table */}
      <div className="navbar">
        <button onClick={() => setActiveTab('upcoming')} className={activeTab === 'upcoming' ? 'active' : ''}>Upcoming Campaigns</button>
        <button onClick={() => setActiveTab('live')} className={activeTab === 'live' ? 'active' : ''}>Live Campaigns</button>
        <button onClick={() => setActiveTab('past')} className={activeTab === 'past' ? 'active' : ''}>Past Campaigns</button>
      </div>

      {/* Table */}
      {renderTable()}
    </div>
  );
};

export default App;
