import React, { useState } from "react";
import { useTranslation } from "react-i18next"; // Import translation hook
import Campaign from "./components/CampaignTable";
import PricingModal from "./components/PricingModal";
import ScheduleModal from "./components/ScheduleModal";
import "./App.css";
import "./i18n"; // Import i18n configuration

const App = () => {
  const { t, i18n } = useTranslation(); // Initialize translation
  const [activeTab, setActiveTab] = useState("upcoming");
  const [showPricingModal, setShowPricingModal] = useState(false);
  const [showScheduleModal, setShowScheduleModal] = useState(false);
  const [selectedCampaign, setSelectedCampaign] = useState(null);

  const handleLanguageSwitch = () => {
    const newLang = i18n.language === "en" ? "fr" : "en";
    i18n.changeLanguage(newLang); // Change language dynamically
  };

  const campaigns = []; // Fetch campaigns based on the selected tab (you can integrate this with your data.json logic)

  const handleOpenPricingModal = (campaign) => {
    setSelectedCampaign(campaign);
    setShowPricingModal(true);
  };

  const handleOpenScheduleModal = (campaign) => {
    setSelectedCampaign(campaign);
    setShowScheduleModal(true);
  };

  const handleCloseModal = () => {
    setShowPricingModal(false);
    setShowScheduleModal(false);
    setSelectedCampaign(null);
  };

  return (
    <div className="App">
      <header className="header">
        <h1>{t("title")}</h1>
        <button className="language-switch" onClick={handleLanguageSwitch}>
          {i18n.language === "en" ? "Switch to French" : "Passer à l'anglais"}
        </button>
      </header>

      <div className="navbar">
        <button
          onClick={() => setActiveTab("upcoming")}
          className={activeTab === "upcoming" ? "active" : ""}
        >
          {t("upcoming")}
        </button>
        <button
          onClick={() => setActiveTab("live")}
          className={activeTab === "live" ? "active" : ""}
        >
          {t("live")}
        </button>
        <button
          onClick={() => setActiveTab("past")}
          className={activeTab === "past" ? "active" : ""}
        >
          {t("past")}
        </button>
      </div>

      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>{t("title")}</th>
            <th>{t("viewPricing")}</th>
            <th>{t("actions")}</th>
          </tr>
        </thead>
        <tbody>
          {campaigns.map((campaign) => (
            <Campaign
              key={campaign.id}
              campaign={campaign}
              onViewPricing={handleOpenPricingModal}
              onSchedule={handleOpenScheduleModal}
            />
          ))}
        </tbody>
      </table>

      {showPricingModal && (
        <PricingModal campaign={selectedCampaign} onClose={handleCloseModal} />
      )}

      {showScheduleModal && (
        <ScheduleModal campaign={selectedCampaign} onClose={handleCloseModal} />
      )}
    </div>
  );
};

export default App;
