import React, { useState } from 'react';
import './App.css';
import data from './data.json';

const App = () => {
  const [activeTab, setActiveTab] = useState('upcoming');
  const [language, setLanguage] = useState('en');  // Language state

  const renderTable = () => {
    const dataForTab = data[activeTab];

    return (
      <table border="1">
        <thead>
          <tr>
            <th>Date</th>
            <th>Campaign</th>
            <th>View</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {dataForTab.map((campaign) => (
            <tr key={campaign.id}>
              <td>{new Date(campaign.createdOn).toLocaleDateString()}</td>
              <td>{campaign.name}</td>
              <td>
                <button>{campaign.price}</button>
              </td>
              <td>
                <div className="actions">
                  <a href={campaign.csv} target="_blank" rel="noopener noreferrer">
                    <img src="https://via.placeholder.com/20" alt="CSV" /> CSV
                  </a>
                  <a href={campaign.report} target="_blank" rel="noopener noreferrer">
                    <img src="https://via.placeholder.com/20" alt="Report" /> Report
                  </a>
                  <button className="schedule">
                    <img src="https://via.placeholder.com/20" alt="Calendar" /> Schedule Again
                  </button>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  };

  return (
    <div>
      <header className="header">
        <h1>Manage Campaign</h1>
      </header>

      {/* Language Switcher */}
      <div className="language-switcher">
        <button onClick={() => setLanguage(language === 'en' ? 'fr' : 'en')}>
          {language === 'en' ? 'Switch to French' : 'Passer au français'}
        </button>
      </div>

      {/* Event Type Buttons in Table */}
      <div className="navbar">
        <button onClick={() => setActiveTab('upcoming')} className={activeTab === 'upcoming' ? 'active' : ''}>Upcoming Campaigns</button>
        <button onClick={() => setActiveTab('live')} className={activeTab === 'live' ? 'active' : ''}>Live Campaigns</button>
        <button onClick={() => setActiveTab('past')} className={activeTab === 'past' ? 'active' : ''}>Past Campaigns</button>
      </div>

      {/* Table */}
      {renderTable()}
    </div>
  );
};

export default App;
