// App.js
import React, { useState } from 'react';
import './App.css';  // Ensure to import the CSS file for styling
import data from './data.json'; // Import the data from data.json

const App = () => {
  const [activeTab, setActiveTab] = useState('upcoming');

  const renderTable = () => {
    const dataForTab = data[activeTab];

    return (
      <table border="1">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Region</th>
            <th>Created On</th>
            <th>Price</th>
            <th>CSV Link</th>
            <th>Report Link</th>
            <th>Image</th>
          </tr>
        </thead>
        <tbody>
          {dataForTab.map((campaign) => (
            <tr key={campaign.id}>
              <td>{campaign.id}</td>
              <td>{campaign.name}</td>
              <td>{campaign.region}</td>
              <td>{new Date(campaign.createdOn).toLocaleDateString()}</td>
              <td>{campaign.price}</td>
              <td><a href={campaign.csv} target="_blank" rel="noopener noreferrer">CSV</a></td>
              <td><a href={campaign.report} target="_blank" rel="noopener noreferrer">Report</a></td>
              <td><img src={campaign.image_url} alt={campaign.name} /></td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  };

  return (
    <div>
      {/* Navbar with buttons for switching tabs */}
      <div className="navbar">
        <button onClick={() => setActiveTab('upcoming')} className={activeTab === 'upcoming' ? 'active' : ''}>
          Upcoming
        </button>
        <button onClick={() => setActiveTab('live')} className={activeTab === 'live' ? 'active' : ''}>
          Live
        </button>
        <button onClick={() => setActiveTab('past')} className={activeTab === 'past' ? 'active' : ''}>
          Past
        </button>
      </div>

      {/* Table to display data based on the selected tab */}
      {renderTable()}
    </div>
  );
};

export default App;
