import React, { useState } from "react";
import ScheduleModal from "./ScheduleModal";
import PricingModal from "./PricingModal";

const CampaignTable = ({ campaigns, updateCampaign }) => {
  const [pricingData, setPricingData] = useState(null);

  const calculateTimeDiff = (date) => {
    const diff = (date - Date.now()) / (1000 * 60 * 60 * 24);
    return diff > 0
      ? `${Math.ceil(diff)} days ahead`
      : `${Math.abs(Math.ceil(diff))} days ago`;
  };

  return (
    <div className="campaign-table">
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Campaign</th>
            <th>Region</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {campaigns.map((campaign) => (
            <tr key={campaign.id}>
              <td>{calculateTimeDiff(campaign.createdOn)}</td>
              <td>
                <img src={campaign.image_url} alt="campaign" />
                {campaign.name}
              </td>
              <td>{campaign.region}</td>
              <td>
                <button onClick={() => updateCampaign(campaign.id)}>Schedule Again</button>
                <button onClick={() => setPricingData(campaign)}>View Pricing</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {pricingData && (
        <PricingModal
          campaign={pricingData}
          onClose={() => setPricingData(null)}
        />
      )}
    </div>
  );
};

export default CampaignTable;
